import react,{useEffect,useState} from 'react';
import Button from 'react-bootstrap/Button';
import Image from 'react-bootstrap/Image';
import { Container, Grid, Col, Row } from 'react-bootstrap';
import {readGoogleAsCSV} from '../core/Config';
import "../css/topic.css";
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useRouteMatch,
    useParams,
    useLocation
  } from "react-router-dom";

export default function Topic(props) {
    let { topicId } = useParams();

    // try {
    //     json = await jsonFromGoogleURL(url, options);
    // } catch (e) {
    //     // even with an error, we make 
    //     // a TimelineConfig because it's 
    //     // the most straightforward way to display messages
    //     // in the DOM
    //     tc = new TimelineConfig();
    //     if (e.name == 'NetworkError') {
    //         tc.logError(new TLError("network_err"));
    //     } else if (e.name == 'TLError') {
    //         tc.logError(e);
    //     } else {
    //         tc.logError(new TLError("unknown_read_err", e.name));
    //     }
    //     callback(tc);
    //     return; // don't process further if there were errors
    // }

        const [topic_list,set_topic_list] = useState([])

        useEffect(() => {
            readGoogleAsCSV(topicId, '')
           .then(data => set_topic_list(data))
        });



    // readGoogleAsCSV(topicId,'').then(res=>{
    //     topic_list= res;
    //     console.log(topic_list);
    //   });

    // if (topic_list===null){
    //     return <div >Hello</div>;
    // }

    return (
        <div>
            <div style={{ textAlign: "center", margin: "5%", fontSize: "1em" }}>{}</div>
            <div style={{ textAlign: "center", margin: "5%", fontSize: "1em" }}>{props.data.topic_word}</div>
            <div style={{ position: "relative"}}>
                <Col>
                    <Image src={props.data.img_src} style={{ width: "150%", height: "auto"}}/>
                    <div className="topic_left_top_tag">SPOTLIGHT</div>
                    <div className="topic_text_container">
                        <div className="topic_headline">{props.data.headline}</div>
                        <div className="topic_summary">{props.data.summary}</div>
                    </div>
                </Col>
            </div>
            <div class="author_row">
            <Container>
            <Row >
                    <Col>
                        <Image src={props.data.author_icon} roundedCircle style={{ width:"20%"  }} />
                    </Col>
                    <Col >
                        <div>{props.data.author_name}</div>
                        <div>{props.data.author_bio}</div>
                    </Col>
            </Row>
            </Container>
            </div>
        </div>
    )
}
